import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Statistics=({clicks})=>{

    const {good,neutral,bad}=clicks
    if(!(good+bad+neutral)){
        return (<>
        <h1>statistics</h1>
        <p>No feedback given</p>
        </>)
    }else{
        return (
            <>
          <h1>statistics</h1>
          <p>good {good}</p> 
          <p>neutral {neutral}</p>
          <p>bad {bad}</p>
          <p>all {(good+neutral+bad)?(good+neutral+bad):0}</p>
          <p>average {(good+neutral+bad)?(good+0*neutral+-1*bad)/(good+neutral+bad):0}</p>
          <p>positive {(good+neutral+bad)?good*100/(good+neutral+bad):0}</p> 
          </>
        )
    }
}

const Button = ({buttonDetails})=>{
 const {text,clicks,handleClick}= buttonDetails
 return (<button onClick={()=>{handleClick(clicks+1)}}> {text} </button>)
}

const App = () => {
  // save clicks of each button to own state
  const [clicks,setClicks] = useState({g:0,n:0,b:0})
  const gClick=(clicks)=>{
      const newClick={
          ...clicks,
          g:clicks.g+1
      }
      setClicks(newClck) 
  }

const nClick=(clicks)=>{
    const newClick={
        ...clicks,
        n:clicks.n+1
    }
    setClicks(newClck) 
}


const bClick=(clicks)=>{
    const newClick={
        ...clicks,
        n:clicks.n+1
    }
    setClicks(newClck) 
}

  return (
    <div>
      <h1>give feedback</h1>
      <Button buttonDetails={{text:"good",clicks,handleClick:gClick}}/>
      <Button buttonDetails={{text:"neutral",clicks,handleClick:nClick}}/>
      <Button buttonDetails={{text:"bad",clicks,handleClick:bClick}}/>
      <Statistics clicks={clicks}/>
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
