import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Statistics=({clicks})=>{

    const {good,neutral,bad}=clicks
    if(!(good+bad+neutral)){
        return (<>
        <h1>statistics</h1>
        <p>No feedback given</p>
        </>)
    }else{
        return (
            <>
          <h1>statistics</h1>
          <p>good {good}</p> 
          <p>neutral {neutral}</p>
          <p>bad {bad}</p>
          <p>all {(good+neutral+bad)?(good+neutral+bad):0}</p>
          <p>average {(good+neutral+bad)?(good+0*neutral+-1*bad)/(good+neutral+bad):0}</p>
          <p>positive {(good+neutral+bad)?good*100/(good+neutral+bad):0}</p> 
          </>
        )
    }
}

const Button = ({clickDetails}) =>{
 const {text,click,clickHandler}=clickDetails
 console.log(clickHandler)
 return (<><button onClick={()=>{ clickHandler(click+1) }}>{text}</button></>)
}

const App = () => {
  // save clicks of each button to own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div>
      <h1>give feedback</h1>
      {/* <button onClick={()=>{setGood(good+1)}}> good </button>
      <button onClick={()=>{setNeutral(neutral+1)}}> neutral </button>
      <button onClick={()=>{setBad(bad+1)}}> bad </button> */}
      <Button clickDetails={{text:"good",click:good,setGood}} />
      <Statistics clicks={{good,neutral,bad}}/>
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
