import React, { useState } from 'react'



const Numbers=(props)=>{
  const getNames =() => props.persons.map((p)=> <li key={p.name}>{p.name}</li>)
  return ( 
    <> 
    <h2>Numbers</h2>
    <ul>{getNames()}</ul>
    </>
  )
}


const App = () => {
  const [ persons, setPersons] = useState([
    { name: 'Arto Hellas' , number: '1231231313'}
  ]) 
  const [newName,setNewName]= useState('')
  const [newNumber,setNewNumber]= useState('')
  const [ newPerson, setNewPerson ] = useState({name:'',number:''})


  const handleNameChange = (event)=>{
    const person={
      ...newPerson,
      name:event.target.value
    }
    setNewName(event.target.value)
    setNewPerson(person)
  }

  const handleNumberChange = (event)=>{
    const person={
      ...newPerson,
      number:event.target.value
    }
    setNewNumber(event.target.value)
    setNewPerson(person)
  }

  const handleClick=(event)=>{
    event.preventDefault()
    if(persons.map(p=>p.name).indexOf(newName)>=0){
      console.log(persons.indexOf(newName))
      alert(`${newName} is already added to phonebook`)
    }else{
      setPersons([...persons].concat({name : newName}))
    }
    setNewName('')
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={handleClick}>
        <div>name: <input value={newPerson} onChange={handleNameChange}/></div>
        <div>number: <input value={newPerson} onChange={handleNumberChange}/></div>
        <div><button type="submit">add</button></div>
      </form>

      <Numbers persons={persons}/>
    </div>
  )
}

export default App
