import React  from 'react'
import service from './DataService'

const Button=(props)=>{

  const clickDel=()=>{
    return ()=>{
      console.log('---*****',props)
      service.del(props.id).then(data=>{
        service.getAll().then(d => props.setP(d)).catch(err=> console.log('fail'))
      })
    }
   }

   return (<button onClick={clickDel()}>delete</button>)
}

const Number=(props)=>{
  console.log(props)
    const getNamesForNumbers =() => props.persons.map((p)=> <li key={p.id}>{p.name} {p.number}</li>)
    const getNamesForSearch =() => props.persons.filter(p => props.smode==='n'?true:p.name.toLowerCase()===props.searchString.toLowerCase()).map((p)=> <li key={p.id}>{p.name} {p.number} <Button  setP={props.setP} id={p.id}/></li>)

      if(props.smode==='y'){
          return <ul>{getNamesForNumbers()}</ul>;
      }else{
        return <ul>{getNamesForSearch()}</ul>;
      }
  }

  export default Number
